type ValidationFunction<T> = (inputs: T) => Record<keyof T, string>;

interface ValidationRule {
  required?: boolean;
  minLength?: number;
  regex?: RegExp;
  customValidation?: (value: string) => string | undefined;
}

const fieldValidationRules: Record<string, ValidationRule> = {
  name: { required: true },
  email: { required: true, regex: /^[^\s@]+@[^\s@]+\.[^\s@]+$/ },
  password: { required: true, minLength: 6 },
  message: { required: true, minLength: 10 },
};

const Validation: ValidationFunction<{ [key: string]: string }> = (inputs) => {
  const errors: { [key: string]: string } = {};



  return errors;
};

export default Validation;
