import React, { useState } from 'react';
import { Box, TextField, Container, Button, Typography, Link, Switch} from "@mui/material";
import Validation from './Validation';
import FormManager from './FormManager';
import { fieldValidationRules } from './ValidationRules';

// type ValidationRules = {
//   [key: string]: (value: string) => string;
// };

const Auth = () => {
  const [isRegister, setIsRegister] = useState(false);
  const initialState = isRegister
  ? { name: "", email: "", password: "", confirmPassword: "" }
  : { email: "", password: "" };

  const { inputs, errors, handleChange, validate, resetForm } = FormManager(initialState);

  const currentRules: Validation = {
    ...fieldValidationRules,
    ...(isRegister && {
      confirmPassword: (value: string) =>
        value !== inputs.password ? "Passwords do not match." : "",
    }),
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (Object.keys(validationErrors).length === 0) {
      if (isRegister) {
        console.log("Register form submitted:", inputs);
      } else {
        console.log("Login form submitted:", inputs);
      }
      resetForm();
    } else {
      console.log("Validation failed:", errors);
    }
  };

    return(
        <>
        <Container>
        <Box
         display="flex"
         flexDirection={"column"}
         alignItems="center"
         justifyContent={"center"}
         maxWidth={600}
         margin="auto"
         marginTop={5}
         padding={3}
        borderRadius={5}
        border={"1px solid #eee"}
        >
        <h2>{isRegister ? "Register" : "Login" }</h2>
        <form onSubmit={handleSubmit}>
          {isRegister && 
        <TextField
                name="name"
                value={inputs.name}
                onChange={handleChange}
                label="Name"
                type={"text"}
                error={Boolean(errors.name)}
                helperText={errors.name}
                margin="dense"
                placeholder="Name"
                variant="outlined"
                fullWidth
            /> 
          }
            <TextField
                name="email"
                label="Email"
                value={inputs.email}
                onChange={handleChange}
                type="email"
                error={Boolean(errors.email)}
                helperText={errors.email}
                margin="dense"
                placeholder="Email"
                variant="outlined"
                fullWidth
            />  
            <TextField
                name="password"
                label="Password"
                value={inputs.password}
                onChange={handleChange}
                type="password"
                error={Boolean(errors.password)}
                helperText={errors.password}
                margin="dense"
                placeholder="Password"
                variant="outlined"
                fullWidth
            /> 
            {isRegister && 
            <TextField
                name="confirmPassword"
                label="Confirm Password"
                value={inputs.confirmPassword}
                onChange={handleChange}
                type="password"
                error={Boolean(errors.confirmPassword)}
                helperText={errors.confirmPassword}
                margin="dense"
                placeholder="Confirm Password"
                variant="outlined"
                fullWidth
            /> 
            }
            <div>
                <Button variant="contained" type="submit" sx={{margin:3}}>{isRegister ? "Register" : "Login"}</Button>
            </div>
            <Switch
          checked={isRegister}
          onChange={() => setIsRegister((prev) => !prev)}
          color="primary"
        />
        <Typography variant="body2">
          {isRegister ? "Already have an account? " : "New here? "}
          <Link
            component="button"
            variant="body2"
            onClick={(e) => {
              e.preventDefault();
              setIsRegister((prev) => !prev);
            }}
          >
            {isRegister ? "Login" : "Register"}
          </Link>
        </Typography>
        </form>     
        </Box>
        </Container>
        </>
    );
}
export default Auth;
