import {useState} from 'react';

const FormManager = (initialState, validateFields) => {
    const [inputs, setInputs] = useState(initialState);
  const [errors, setErrors] = useState(
    Object.keys(initialState).reduce((acc, key) => ({ ...acc, [key]: "" }), {})
  );

    const handleChange = (e:any) => {
          setInputs((prevState) => ({ 
              ...prevState, 
              [e.target.name]: e.target.value 
          }));
          setErrors((prevErrors) => ({
              ...prevErrors,
              [e.target.name]: ""
            }));
    };
    const validate = () => {
        const validationErrors = validateFields(inputs);
        setErrors(validationErrors);
        return Object.keys(validationErrors).every((key) => !validationErrors[key]);
    };
    const resetForm = () => setInputs(initialState);
    return { inputs, errors, handleChange, validate, resetForm };
}
export default FormManager;
