import React, { useState } from 'react';
import { Box, TextField, Container, Button, Typography, Link } from "@mui/material";
import Validation from './Validation';
import FormManager from './FormManager';
import { fieldValidationRules } from './ValidationRules';
const Auth = () => {
  const [isRegister, setIsRegister] = useState(false);
  const initialState = { name: "", email: "", password: "", confirmPassword:"" };

  const { inputs, errors, handleChange, validate, resetForm } = FormManager(initialState);

  const currentRules = {
    ...fieldValidationRules,
    ...(isRegister && {
      confirmPassword: (value:string) =>
        value !== inputs.password ? "Passwords do not match." : "",
    }),
  };

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    console.log(inputs);
    console.log(errors);
    if (validate(Validation)) {
      console.log("login/Register form submitted:", inputs);
       resetForm(); 
    } else {
      console.log("Validation failed:", errors);
    }
  };

    return(
        <>
        <Container>
        <Box
         display="flex"
         flexDirection={"column"}
         alignItems="center"
         justifyContent={"center"}
         maxWidth={600}
         margin="auto"
         marginTop={5}
         padding={3}
        borderRadius={5}
        border={"1px solid #eee"}
        >
        <h2>{isRegister ? "Register" : "Login" }</h2>
        <form onSubmit={handleSubmit}>

        <TextField
                name="name"
                value={inputs.name}
                onChange={handleChange}
                label="Name"
                type={"text"}
                error={Boolean(errors.name)}
                helperText={errors.name}
                margin="dense"
                placeholder="Name"
                variant="outlined"
                fullWidth
            /> 
            <TextField
                name="email"
                label="Email"
                value={inputs.email}
                onChange={handleChange}
                type="email"
                error={Boolean(errors.email)}
                helperText={errors.email}
                margin="dense"
                placeholder="Email"
                variant="outlined"
                fullWidth
            />  
            <TextField
                name="password"
                label="Password"
                value={inputs.password}
                onChange={handleChange}
                type="password"
                error={Boolean(errors.password)}
                helperText={errors.password}
                margin="dense"
                placeholder="Password"
                variant="outlined"
                fullWidth
            /> 
            <TextField
                name="confirmPassword"
                label="Confirm Password"
                value={inputs.confirmPassword}
                onChange={handleChange}
                type="password"
                error={Boolean(errors.confirmPassword)}
                helperText={errors.confirmPassword}
                margin="dense"
                placeholder="Confirm Password"
                variant="outlined"
                fullWidth
            /> 
            <div>
                <Button variant="contained" type="submit" sx={{margin:3}}>Submit</Button>
            </div>
            <Switch
          checked={isRegister}
          onChange={() => setIsRegister((prev) => !prev)}
          color="primary"
        />
        <Typography variant="body2">
          {isRegister ? "Already have an account? Login" : "New here? Register"}
        </Typography>
        </form>     
        </Box>
        </Container>
        </>
    );
}
export default Auth;
