const Validation: ValidationFunction<{ [key: string]: string }> = (inputs) => {
  const errors = Object.keys(inputs).reduce((acc, key) => {
    const value = inputs[key];
    if (!value.trim()) {
      acc[key] = ` ${key}. is required`;
    }
    return acc;
  }, {} as Record<string, string>);
  return errors;
}
