import React, { useState } from "react";
import { Box, FormControl, InputLabel, Input, FormHelperText, TextField, Container, CssBaseline, Button } from '@mui/material';
import { setIn } from "formik";
// import { useForm } from 'react-hook-form';
// import * as Yup from 'yup';
const ContactForm = () => {
    const [inputs, setInputs] = useState({
        name: "",
        email: "",
        password:"",
        message: ""
    });
    const [errors, setErrors] = useState({
        name: "",
        email: "",
        password: "",
        message: ""
      });

    const handleChange = (e:any) => {
        setInputs((prevState) => ({ 
            ...prevState, 
            [e.target.name]: e.target.value 
        }));
        setErrors((prevErrors) => ({
            ...prevErrors,
            [e.target.name]: "" // Clear error for the field when user starts typing
          }));
    };
    const validate = () => {
        let isValid = true;
        const newErrors: Record<string> = {};

        if (!inputs.name.trim()) {
          newErrors.name = "Name is required.";
          isValid = false;
        }

        setErrors(newErrors);
        return isValid;
    }

    const handleSubmit = (e: any) => {
        e.preventDefault();
        console.log(inputs);
    };
    return(
        <>
        <Container>
        <Box
         display="flex"
         flexDirection={"column"}
         alignItems="center"
         justifyContent={"center"}
         maxWidth={600}
         margin="auto"
         marginTop={5}
         padding={3}
        borderRadius={5}
        border={"1px solid #eee"}
        >
        <h2>Contact</h2>
        <form onSubmit={handleSubmit}>
            <TextField
                name="name"
                value={inputs.name}
                onChange={handleChange}
                label="Name"
                type={"text"}
                required
                error={Boolean(errors.name)}
                helperText={errors.name}
                margin="dense"
                placeholder="Name"
                variant="outlined"
                fullWidth
            /> 
            <TextField
                name="email"
                value={inputs.email}
                onChange={handleChange}
                label="Email"
                type={"email"}
                required
                margin="dense"
                placeholder="Email"
                variant="outlined"
                fullWidth
            />  
            <TextField
                name="password"
                value={inputs.password}
                onChange={handleChange}
                label="Password"
                type={"passward"}
                required
                margin="dense"
                placeholder="Password"
                variant="outlined"
                fullWidth
            /> 
            <TextField
                name="message"
                value={inputs.message}
                onChange={handleChange}
                label="Multiline"
                multiline
                rows={4}
                margin="dense"
                placeholder="Message"
                fullWidth
            /> 
            <div>
                <Button variant="contained" type="submit" sx={{margin:3}}>Submit</Button>
            </div>
        </form>     
        </Box>
        </Container>
        </>
    );
}

export default ContactForm;
